mapper.select:
select

from zhcs_zx_article
ORDER BY CREATION_DATE LIMIT ${first}, ${pageSize}

select

from zhcs_zx_article
where CREATION_DATE > #{lastCreateTime} LIMIT ${first},${pageSize}

select

from zhcs_zx_article
WHERE MODIFICATION_DATE > CREATION_DATE  LIMIT ${first},${pageSize}

select

from zhcs_zx_article
WHERE MODIFICATION_DATE > #{lastUpdateTime}  LIMIT ${first},${pageSize}
